import{_ as p}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as r,e as t}from"./app-2a5ea3cb.js";const a={},c=t('<h1 id="我十七" tabindex="-1"><a class="header-anchor" href="#我十七" aria-hidden="true">#</a> 我十七</h1><p>——送给十七岁的自己</p><hr><p>我十七</p><p>这十七过得并不容易</p><p>翻山越岭</p><p>却总是寻不到你</p><p>人们常说努力能收获光明</p><p>可我却换来风和雨</p><hr><p>我十七</p><p>这梦想还未曾凋零的花季</p><p>是不是内心有了伤痛</p><p>才能看清最真实的自己</p><p>常常幻想梦的颜色</p><p>可它的彼岸在哪里？</p><hr><p>今年我十七</p><p>十七经历的那些风和雨</p><p>或许就是岁月最好的赠礼</p><p>来吧风和雨</p><p>往事纵然刻下再多痕迹</p><p>都将随风飘荡而去</p><hr><p>今年我十七</p><p>是一生只有一次的十七</p><p>不能学着说去放弃</p><p>追寻光的影子</p><p>所有的苦难与背负尽头</p><p>都是行云流水般的此世光阴</p><hr><p>今年我十七</p><p>还有很多轮回的十七</p><p>星辰奔波数万夜</p><p>不怨诉语</p><p>愿时时能看见</p><p>柳暗花明，日落潮汐</p><p>把最好的十七</p><p>托付给自己</p><hr><p>2017.2.21</p>',41),o=[c];function _(h,n){return e(),r("div",null,o)}const d=p(a,[["render",_],["__file","我十七.html.vue"]]);export{d as default};
